import { Component, Input } from "@angular/core";
import { Personaje } from "../interfaces/Personaje";

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html'
})
export class DbzMainPageComponent{

  personajes : Personaje[] = [
    {
      nombre: 'Gohan',
      poder: 15000
    },
    {
      nombre: 'Trunks',
      poder: 8500
    },
    {
      nombre: 'Gotens',
      poder: 8500
    }
  ]

  public onNewCharacter(pj: any): void {
    this.personajes.push(pj);
  }
}
<h1>DBZ MAIN PAGE</h1>
<hr>
<div class="row">
  <div class="col-6">
    <app-dbz-list [personajes]="personajes"></app-dbz-list>
  </div>
  <div class="col-6">
    <app-dbz-add-character (onNuevoPersonaje)="onNewCharacter($event)"></app-dbz-add-character>
  </div>
</div>
