import { Component, EventEmitter } from '@angular/core';
import { Personaje } from '../../interfaces/Personaje';

@Component({
  selector: 'app-dbz-add-character',
  templateUrl: './add-character.component.html',
  styleUrls: ['./add-character.component.css']
})
export class AddCharacterComponent {

  public onNewCharacterEmit: EventEmitter<Personaje> = new EventEmitter();

  nuevoPersonaje: Personaje = {
    nombre: '',
    poder: 20
  };

  EmitirPersonaje(): void {
    if (this.nuevoPersonaje.nombre.trim().length === 0) return;
    this.onNewCharacterEmit.emit({...this.nuevoPersonaje});
    this.nuevoPersonaje.nombre = '';
    this.nuevoPersonaje.poder = 0;
  }
}
<form class="form-group" (ngSubmit)="EmitirPersonaje()">
  <span>Nombre</span>
  <input name="nombre" [(ngModel)]="nuevoPersonaje.nombre" type="text" class="form-control" />
  <span>Poder</span>
  <input name="poder" [(ngModel)]="nuevoPersonaje.poder" type="text" class="form-control mt-2" />
  <button type="submit" class="btn btn-primary mt-2">Agregar</button>
</form>
