import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Personaje } from "../../interfaces/Personaje";

@Component({
  selector: 'app-dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  @Output()
  onDeletePersonaje: EventEmitter<number> = new EventEmitter();

  @Input()
  personajes : Personaje[] = []

  onDeleteCharacter(index: number): void {
    this.onDeletePersonaje.emit(index);
  }
}
<h5>Personajes</h5>
<ul class="list-group">
  <li class="list-group-item d-flex justify-content-between align-items-center"
  *ngFor="let personaje of personajes;
  first as IsFist;
  last as IsLast;
  index as Index">

  <div>
    <span [ngClass]="{
        'text-primary': IsFist,
        'text-warning': IsLast
      }">{{ personaje.nombre }}: <b>{{personaje.poder}}</b></span>
  </div>

    <button class="btn btn-danger" (click)="onDeleteCharacter(Index)">X</button>
  </li>
</ul>
