import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})
export class HeroComponent {

  public nombre: string = 'IronMan';
  public age: number = 40;
  public metodo: string = 'IronMan Metodo';

  public get CapitalizeName(): string {
    return this.nombre.toUpperCase();
  }

  GetHeroDescription(): string {
    return `${this.nombre } - ${ this.age }`;
  }
}
<h1>Spiderman</h1>

<dl>
  <td>Nombre:</td>
  <dd> {{nombre}}</dd>

  <td>Edad:</td>
  <dd> {{age}}</dd>

  <td>Metodo:</td>
  <dd> {{GetHeroDescription}}</dd>

  <td>Capitalizado:</td>
  <dd> {{CapitalizeName}}</dd>
</dl>

<button class="btn btn-primary mx-2">Cambiar Nombre</button>
<button class="btn btn-primary">Cambiar Edad</button>
