import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {
  heroNames: string[] = ['Batman','Superman', 'Hulk', 'Thor'];
  heroeBorrado: string = '';

  BorrarHeroe(): void{
    this.heroeBorrado = this.heroNames.pop()!.toString();
  }
}
<h3>Lista de Heroes</h3>

<button
  (click)="BorrarHeroe()"
  class="btn btn-outline-danger">
  Borrar Heroe
</button>

<h3>Heroe borrado: </h3>
<small *ngIf="heroeBorrado === ''" class="text-danger">{{heroeBorrado}}</small><br>
<small *ngIf="heroeBorrado !== ''">No ha borrado nada</small>

<ul class="mt-2 list-group">
  <li class="list-group-item" *ngFor="let name of heroNames"><b>{{name}}</b></li>
</ul>
