import { Component, Input } from '@angular/core';
import { Gif } from '../../Interfaces/gifs.interfaces';

@Component({
  selector: 'gif-gifcard',
  templateUrl: './gifcard.component.html',
  styleUrls: ['./gifcard.component.css']
})
export class GifcardComponent {

  @Input()
  public Gif!: Gif;

}
<div class="card">
  <div class="card-img"><img class="img img-fluid" [src]="Gif?.FixedHeightDownsampled?.url" />
  </div>
  <div class="card-body bg-dark"><span class="text-light">{{Gif?.title}}</span>
  </div>
</div>
