import { Component, ViewChild, ViewChildren } from '@angular/core';
import { GifsService } from '../../Services/gift.service';

@Component({
  selector: 'gifs-searchbox',
  templateUrl: './searchbox.component.html',
  styleUrls: ['./searchbox.component.css']
})
export class SearchboxComponent {

  constructor(private _gifService:GifsService) { }

  @ViewChild('txtSearchBox', {static: fail}) txtSearchBox!: HTMLInputElement;

  public onSearch(event: KeyboardEvent, query: string): void {
    if (event.key !== 'Enter') return;
    console.log(query);
    this._gifService.addQuery(query);
    this.txtSearchBox.value = '';
  }

}
