import { Component, Input, OnInit } from '@angular/core';

@Component({
    selector: 'shared-lazy-image',
    templateUrl: './lazyImage.component.html',
    styleUrls: ['./lazyImage.component.css']
})
export class LazyImageComponent implements OnInit {

  constructor() { }

  @Input()
  url!: string

  @Input()
  alt: string = '';


  public hasLoaded: boolean = false;

  ngOnInit(): void {
    if (!this.url) throw new Error('LazyImageComponent: url is required');
  }

  public onLoad(): void {
    setTimeout(() => {
      this.hasLoaded = true;
    }, 1000);
  }

}
<img
  class="img img-fluid card-img-top animate__animated animate__fadeIn"
  [src]="url"
  [alt]="alt"
  (load)="onLoad()"
  [ngStyle]="{display: hasLoaded ? 'block' : 'none'}" />

<img *ngIf="!hasLoaded"
  class="img img-fluid card-img-top "
  src="assets/loader.gif"
  height="35"
  width="35"
/>
