import { Component, ViewChild } from '@angular/core';
import { GifsService } from '../../Services/gift.service';

@Component({
  selector: 'gifs-searchbox',
  templateUrl: './searchbox.component.html',
  styleUrls: ['./searchbox.component.css']
})
export class SearchboxComponent {

  constructor(private _gifService:GifsService) { }

  searchQuery: string = '';

  public onSearch(event: KeyboardEvent, query: HTMLInputElement): void {
    if (event.key !== 'Enter') return;
    this._gifService.addQuery(query.value);
    this.searchQuery = '';
  }

}
<h3>Search Gifs</h3>
<input
  #txtSearchBoxControl
  value="{{searchQuery}}"
  type="text"
  class="form-control"
  placeholder="Search Tag..."
  (keyup)="onSearch($event, txtSearchBoxControl.value);"/>
